import React, { Component } from 'react'
import ProlductItems from '../../Containers/ProlductItems'
import { fetchCourse, fetchCategories } from '../../Redux/Action/course'
import { connect } from 'react-redux'
import { courseService } from '../../Services'

class Course extends Component {
    componentDidMount() {
        this.props.dispatch(fetchCategories())
    }
    //khi bấm vào categories thì phải chạy hàm setCourseCategory để dispatch action lên store
    //truyền id của categories đó vào action =>set động =>fetch khóa học categories đó
    _chooseCategories = id => {
        this.props.dispatch(courseService.setCourseCategories(id))
    }
    render() {
        return (
            <>
                <section className="popular">
                    <div className="container-fluid">
                        <div className="row">
                            <div className="col-sm-12 col-md-12 col-lg-12 col-xl-12 popular__content">
                                <ul className="nav nav-pills">
                                    {/* {this.props.courseCategories.map((item, i) => {
                                        return (
                                            <li key={i} className="nav-item"><a className={this.props.choosenCategories === item.maDanhMuc
                                                ? "nav-link active show"
                                                : "nav-link"}
                                                data-toggle="pill"
                                                href={this.props.choosenCategories}
                                                onClick={()=>this._chooseCategories(item.maDanhMuc)}
                                                >
                                                {item.tenDanhMuc}
                                                
                                            </a>
                                            </li>
                                        )
                                    })} */}

                                </ul>
                                <div className="tab-content">
                                    <div className="tab-pane container active show" id="didong">
                                        {/* PRODUCT ITEMS */}
                                        <div className="product__items d-flex justify-content-between">
                                            {this.props.coursesList.map((item, i) => {
                                                return (
                                                    <ProlductItems key={i} item={item} />
                                                )
                                            })}

                                        </div>
                                    </div>
                                    <div className="tab-pane container fade" id="frontend">
                                        <div className="product__items d-flex justify-content-between">
                                            <ProlductItems />
                                        </div>
                                    </div>
                                    <div className="tab-pane container fade " id="backend">
                                        <div className="product__items d-flex justify-content-between">
                                            <ProlductItems />
                                        </div>
                                    </div>
                                    <div className="tab-pane container fade" id="javascript">
                                        <div className="product__items d-flex justify-content-between">
                                            <ProlductItems />
                                        </div>
                                    </div>
                                    <div className="tab-pane container fade" id="java">
                                        <div className="product__items d-flex justify-content-between">
                                            <ProlductItems />
                                        </div>
                                    </div>
                                </div>
                            </div>,
                        </div>
                    </div>
                </section>
            </>
        )
    }
}
const mapStateToProps = state => {
    return {
        courseCategories: state.course.categories,
        choosenCategories: state.course.choosenCategory,
        coursesList:state.course.courses
    }
}
export default connect(mapStateToProps, null)(Course)
