import React, { Component } from 'react'
import { fetchUsers } from '../../Redux/Action/user'
import { connect } from 'react-redux'
import { userService } from '../../Services'
import { createAction } from '../../Redux/Action'
import { FETCH_USERS } from '../../Redux/Action/type'
class UserItem extends Component {
    componentDidMount() {
        this.props.dispatch(fetchUsers())
    }
    showItem=()=>{
        this.props.userList.map((user,i)=>{
            console.log(user);
            
        })
    }
    render() {
        console.log(this.props.userList);
        
        let { userList } = this.props

        return (
        <>
        {this.showItem()}
            <tr>
                <td>
                    <div className="name__wrapper d-flex">
                        <img src="/img/avatar.png" alt="avatar" />
                        <div className="course__name ml-3">
                            <h6>{userList.taiKhoan}</h6>
                            <span className="badge badge-info">member</span>
                        </div>
                    </div>
                </td>
                <td className="pt-4">
                    <h6>{userList.hoTen}</h6>

                </td>
                <td className="pt-4">
                    {userList.soDT}
                </td>
                <td className="pt-4">
                    {userList.email}
                </td>
                <td className="pt-4">
                    <button data-toggle="modal" data-target="#courseModal" className="btn btn-info mr-2">
                        <i className="far fa-edit" aria-hidden="true" />
                    </button>
                    <button className="btn btn-danger">
                        <i className="far fa-trash-alt" aria-hidden="true" />
                    </button>
                </td>
            </tr>
            </>
        )


    }
}
const mapStateToProps = state => {
    return {
        userList: state.user.userList
    }
}
export default connect(mapStateToProps, null)(UserItem)
