    render() {
        return (
            <form onSubmit={this.onSubmit}>
                <div className="input-group">
                    <input type="text" className="form-control" placeholder="Tìm Kiếm Khóa Học"
                        onChange={this.onChange}
                        value={this.state.keyword}
                    />
                    <div className="search__result">
                        <div className="search__form">
                            {this.showSearchKeyword(this.props.allCourse, this.props.display)}
                        </div>
                    </div>
                    <div className="input-group-prepend">
                        <span className="input-group-text"><i className="fa fa-search" /></span>
                    </div>
                </div>
            </form>
        )
    }
}
const mapStateToProps = state => ({
    allCourse: state.course.courses,
    display: state.displaySearch.searchKeyword
});

const mapDispatchToProps = dispatch => {
    return {
        handleSearch: keyword => {
            dispatch({
                type: SEARCH_COURSES,
                payload: keyword
            });
        }
    };
};
connect(mapStateToProps, mapDispatchToProps)(SearchCouse)
