import React, { Component } from 'react'
import { Link } from 'react-router-dom'
export default class SearchItem extends Component {
    render() {
        let { course } = props;
        return (
            <Link to={`/detailcontent/${course.maKhoaHoc}`}>
                <div className="item__img"><img src={course.hinhAnh} alt="item" />
                    <div className="img__overlay" />
                </div>
                <div className="item__info">
                    <h5>{course.tenKhoaHoc ? course.tenKhoaHoc.substr(0, 25) + "..." : course.tenKhoaHoc}</h5>
                </div>
            </Link>

        )
    }
}
