let initialState = {
    categories: [],
    choosenCategory: "frontend",//lưu giá trị là mã danh mục
    courseCategories: [],
    courses: [],
    // couseDetail: null
}
const CourseReducer = (state = initialState, action) => {
    switch (action.type) {
        case "FETCH_CATEGORIES": {
            state.categories = action.payload
            return { ...state }
        }
        case "FETCH_COURSE_CATEGORIES": {
            state.courseCategories = action.payload
            return { ...state }
        }
        case "FETCH_COURSES": {
            // console.log(action.payload);

            state.courses = action.payload
            return { ...state }
        }
        // case "FETCH_COURSE_DETAIL": {
        //     // console.log(action.payload);

        //     state.couseDetail = action.payload
        //     return { ...state }
        // }
        default: return state; //vào cái gì ra cái đó,ko thay đổi, k chỉnh sửa
    }
}
export default CourseReducer
