import * as type from "../Action/type";

let initialState = {
    categories: [],
    choosenCategory: "BackEnd",//lưu giá trị là mã danh mục
    courseCategories: [],
    courses: [{
        "maKhoaHoc": "a01",
        "biDanh": "a01",
        "tenKhoaHoc": "Node.js, Express & MongoDB Dev to Deployment",
        "moTa": "In this 8.5 hour course you will learn by example building 2 real world server-side applications from scratch all the way up to deployment with a real domain. No more confusion about how to build a Node app for production and not just on your localhost.",
