        },
        "danhMucKhoaHoc": {
            "maDanhMucKhoahoc": "development",
            "tenDanhMucKhoaHoc": "development"
        }
    },
    {
        "maKhoaHoc": "a02",
        "biDanh": "a02",
        "tenKhoaHoc": "The Web Developer Bootcamp & Data Analytics",
        "moTa": "Hi! Welcome to the Web Developer Bootcamp, the only course you need to learn web development. There are a lot of options for online developer training, but this course is without a doubt the most comprehensive and effective on the market.",
        "luotXem": 155,
        "hinhAnh": "/img/item2.jpg",
