        "ngayTao": "10/10/2019",
        "soLuongHocVien": 0,
        "nguoiTao": {
            "taiKhoan": "Admin",
            "hoTen": "Hoang An",
            "maLoaiNguoiDung": "GV",
            "tenLoaiNguoiDung": "Giáo vụ"
        },
        "danhMucKhoaHoc": {
            "maDanhMucKhoahoc": "development",
            "tenDanhMucKhoaHoc": "development"
        }
    }],
    couseDetail: null,
}
const CourseReducer = (state = initialState, action) => {
    switch (action.type) {
        case type.FETCH_CATEGORIES: {
            // console.log(action.payload);          
            state.categories = action.payload
            return { ...state }
        }
        case type.FETCH_COURSE_CATEGORIES: {
            state.courseCategories = action.payload
            return { ...state }
        }
        case type.FETCH_COURSES: {
            state.courses = action.payload
            return { ...state }
        }
        case type.FETCH_COURSES_DETAIL: {
            state.couseDetail = action.payload
            return { ...state }
        }
        default: return state; //vào cái gì ra cái đó,ko thay đổi, k chỉnh sửa
    }
}
export default CourseReducer
