let initialState = {
    cart: []
}
const CartReducer = (state = initialState, action) => {
    switch (action.type) {
        case "ADD_TO_CART": {
            // console.log(action.itemCourse);

            let cartUpdate = [...state.cart,soLuong=1];
            let index = cartUpdate.findIndex(itemCourse => itemCourse.maKhoaHoc === action.itemCourse.maKhoaHoc);
            if (index !== -1) {
                cartUpdate[index].soLuong+=1;
            }
            else {
                cartUpdate.push(action.itemCourse)
            }
            state.cart = cartUpdate;
            return { ...state }
        }
        case "DELETE_TO_CART":{
            // let cartUpdate=[...state.cart];
            // let index=cartUpdate.findIndex(id=>id.maKhoaHoc==action.maKhoaHoc)
        }
        default: return state; //vào cái gì ra cái đó,ko thay đổi, k chỉnh sửa
    }
}
export default CartReducer
