import { userService } from "../../Services"
import { createAction } from "."
import { FETCH_CREDENTIALS } from "./type"
import { notify } from "../../Services/notify/Notify"

export const fetchCredentials=user=>{
    return dispatch=>{
        userService.logIn(user)
        .then(res=>{
            dispatch(createAction(FETCH_CREDENTIALS,res.data))
            localStorage.setItem('credentials',JSON.stringify(res.data))//gửi taikhoan lên localstore

        
            notify('success','ĐĂNG NHẬP THÀNH CÔNG')
        })
        .catch(err=>{
            console.log(err);  
            notify('error','ĐĂNG NHẬP THẤT BẠI')        
        })
    }
}
