import React, { Component } from 'react'
import ProductItems from './ProductItems';
import { SEARCH_COURSES } from '../Redux/Action/type';
import { connect } from 'react-redux'
// import SearchItem from '../Components/SearchItem/SearchItem';
export default class SearchCouse extends Component {
    constructor(props) {
        super(props);
        this.state = {
            keyword: this.props.searchKeyword
        };
    }
    showSearchKeyword = (course, searchKeyword) => {
        let res = null;
        if (searchKeyword.length > 0 && searchKeyword.length !== "") {
            course = course.filter(course => course.tenKhoaHoc.toLowerCase()
                .indexOf(searchKeyword.toLowerCase().trim()) !== -1);
            res = course.map((item, i) => {
                return (
                    <ProductItems key={i} item={item} />
                )


            })
        }
        return res;
    }
    onChange = e => {
        this.setState(
            {
                keyword: e.target.value
            },
            () => {
                this.props.handleSearch(this.state.keyword);
            }
        );
    };
    onSubmit = e => {
        e.preventDefault();
        this.props.handleSearch(this.state.keyword);
        this.props.history.push("/detail");
    };

    render() {
        let { courses } = this.props;
        let { searchKeyword } = this.props.display;
        return (
            <form action>
                <div className="input-group">
                    <input type="text" className="form-control" placeholder="Tìm Kiếm Khóa Học"
                        onChange={this.onChange}
                        value={this.state.keyword}
                    />
                    <div className="search__result">
                        <div className="search__form">
                            {this.showSearchIem(courses, searchKeyword)}
                        </div>
                    </div>
                    <div className="input-group-prepend">
                        <span className="input-group-text"><i className="fa fa-search" /></span>
                    </div>
                </div>
            </form>
        )
    }
}
const mapStateToProps = state => ({
    courses: state.course.course,
    display: state.displaySearch.searchKeyword
});

const mapDispatchToProps = dispatch => {
    return {
        handleSearch: keyword => {
            dispatch({
                type: SEARCH_COURSES,
                payload: keyword
            });
        }
    };
};
connect(mapStateToProps, mapDispatchToProps)(SearchCouse)
